import React, { useEffect, useState } from "react";
import ReactMarkdown from "react-markdown";

function Blog() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // Obtener una lista de archivos .md en la carpeta posts
    const requireContext = require.context("./posts");
    const posts = requireContext.keys().map((filename) => ({
      filename,
      content: requireContext(filename).default,
    }));
    setPosts(posts);
  }, []);

  return (
    <div>
      <h1>Mis publicaciones</h1>
      {posts.map((post) => (
        <div key={post.filename}>
          <h2>{post.filename}</h2>
          <ReactMarkdown>{post.content}</ReactMarkdown>
        </div>
      ))}
    </div>
  );
}

export default Blog;
